building_function._fn:
function(errs, template, curr_obj, all_templates, root_obj, hierarchy, rows, cols) {
		var obj = {}; 
		obj.display_name = template.element.short_name; 
		obj.form_info = template.element.summary;
		a2_copy(template.element.form_model, obj);
		obj.categories = a2_csv_to_array(obj.categories);
		obj.sub_keys = a2_csv_to_array(obj.sub_keys);
  		if (0 == (obj.sub_keys || []).length) obj.sub_keys = undefined;
		obj.filters = a2_csv_to_array(obj.filters);
		obj.child_filters = a2_csv_to_array(obj.child_filters);
		obj.schema = []; 
		curr_obj.push(obj); 
		return obj;  
}

function(errs, template, curr_obj, all_templates, root_obj, hierarchy, rows, cols) {
