function(errs, template, curr_obj, all_templates, root_obj, hierarchy, rows, cols) {
  var new_obj = { inputs: [] };
  new_obj.name = template.element.short_name;
  new_obj.lock_to_nodes = template.element.form_model.lock_to_nodes;
  new_obj.analytic_type = template.element.form_model.analytic_type;
  new_obj.dependencies = a2_optional_array_from_csv(template.element.form_model.dependencies);
  new_obj.analytic_technology_name_or_id = template.element.form_model.analytic_technology_name_or_id;
  new_obj.library_names_or_ids = template.element.form_model.library_names_or_ids;
  new_obj.node_list_rules = a2_optional_array_from_csv(template.element.form_model.node_list_rules);
  new_obj.config = {};
  //TODO spark/python config
  curr_obj.jobs.push(new_obj);
  eturn new_obj;
}

